using OurTravel.Shared.Entities;

namespace OurTravel.API.Data
{
    public class SeedDb
    {
        private readonly DataContext _context;

        public SeedDb(DataContext context)
        {
            _context = context;
        }

        public async Task SeedDbAsync()
        {
            await _context.Database.EnsureCreatedAsync();
            await CheckCountriesAsync();
        }

        private async Task CheckCountriesAsync()
        {
            if (!_context.Countries.Any())
            {
                _context.Countries.Add(new Country
                {
                    Name = "Colombia",
                    Flag = "https://upload.wikimedia.org/wikipedia/commons/thumb/2/21/Flag_of_Colombia.svg/2560px-Flag_of_Colombia.svg.png",
                    Capital = "Bogotá",
                    Description = "Colombia is a country located in the northwestern part of South America, bordered by the Caribbean Sea to the north, Venezuela and Brazil to the east, Peru and Ecuador to the south, and Panama to the northwest. It also shares maritime borders with Costa Rica, Nicaragua, Honduras, Jamaica, the Dominican Republic, and Haiti. The country's diverse geography ranges from tropical rainforests in the Amazon to the highlands of the Andes mountains.",
                    States = new List<State> {
                        new State
                        {
                            Name = "Antioquia",
                            Cities = new List<City>
                            {
                                new City
                                {
                                    Name = "Medellín",
                                    Flag = "data:image/png;base64,",
                                    Description = "Medellín is the capital city of Antioquia, located in Colombia. Nestled within the Andes Mountains, it boasts a temperate climate, earning the nickname \"City of Eternal Spring\". Historically notorious for drug cartels, the city has transformed over the years. Today, Medellín is celebrated for its innovation, culture, and urban development projects. Tourists are drawn to its festivals, museums, and vibrant nightlife. Its Metrocable system, a public transit cable car, showcases Medellín's commitment to innovative urban solutions."

                                },
                                new City
                                {
                                    Name = "Río Negro",
                                    Flag = "data:image/png;base64,",
                                    Description = "Río Negro is a municipality located in Antioquia, Colombia. It's known for its picturesque landscapes and mild climate. Positioned close to Medellín, it has become a residential and recreational hub. The José María Córdova International Airport, serving Medellín, is situated here. Río Negro reflects a blend of urban development and preserved natural areas. Its rich culture is evident in local festivals and architectural heritage. The region supports diverse agricultural activities, with coffee growing being prominent. Its proximity to Medellín makes it a strategic economic zone in Antioquia."
                                },
                                new City
                                {
                                    Name = "Envigado",
                                    Flag = "data:image/png;base64,",
                                    Description = "Envigado is a municipality located in the department of Antioquia, Colombia, adjacent to Medellín. Historically, it started as a colonial town with deep religious and cultural roots. Today, it blends urban modernity with traditional Antioquian architecture. Known for its educational institutions, Envigado has a reputation for academic excellence. The area's green parks and recreational zones, like La Ceja, are beloved by locals. It's also famous for its well-organized urban development and public services. The local cuisine, crafts, and festivals reflect the rich cultural heritage of the region."
                                },
                                new City
                                {
                                    Name = "Itagüi",
                                    Flag = "data:image/png;base64,",
                                    Description = "Itagüí is a bustling municipality in the department of Antioquia, Colombia, situated just south of Medellín. Historically rooted in textiles, it remains a hub for clothing manufacturing. The city boasts a mix of urban landscapes and green spaces like the Ditaires Park. Itagüí is recognized for its impressive urban development and public transportation. The local community values education, as seen in its numerous institutions. Cultural events, especially dance and music festivals, are an integral part of life here. Over the years, Itagüí has transitioned from industrial roots to a more diversified economic base."
                                },
                                new City
                                {
                                    Name = "Bello",
                                    Flag = "https://upload.wikimedia.org/wikipedia/commons/thumb/7/76/Flag_of_Bello_%28Antioquia%29.svg/800px-Flag_of_Bello_%28Antioquia%29.svg.png",
                                    Description = "Bello is the second-largest city in Antioquia, Colombia, bordering Medellín to the north. As part of the Metropolitan Area of Medellín, it's a significant industrial and commercial center. The city combines modern urban landscapes with lush green spaces like Cerro Quitasol. Bello's history is rich, with indigenous roots predating Spanish colonization. It boasts numerous educational institutions and cultural landmarks. Local festivals and events celebrate the city's vibrant heritage. Bello's rapid urban growth is balanced by its commitment to sustainability and community welfare."
                                },
                                new City
                                {
                                    Name = "Copacabana",
                                    Flag = "https://upload.wikimedia.org/wikipedia/commons/thumb/e/e4/Flag_of_Copacabana_%28Antioquia%29.svg/2560px-Flag_of_Copacabana_%28Antioquia%29.svg.png",
                                    Description = "Copacabana is a picturesque municipality in Antioquia, Colombia, nestled north of Medellín. Known for its serene landscapes, it offers a peaceful retreat from urban hustle. The town is anchored around its main square and historic Basilica of Our Lady of the Assumption. Copacabana boasts colonial architecture and cobblestone streets, hinting at its rich past. Locals celebrate various festivals, showcasing vibrant cultural traditions. The surrounding hills and green spaces are popular among nature enthusiasts. Despite its tranquil demeanor, Copacabana has modern amenities, making it a sought-after residential locale."
                                },
                            },
                            Flag = "https://upload.wikimedia.org/wikipedia/commons/thumb/3/33/Flag_of_Antioquia_Department.svg/1200px-Flag_of_Antioquia_Department.svg.png",
                            Description = "Antioquia is a diverse department in northwest Colombia, with Medellín as its capital. Known for its verdant mountains and coffee plantations, its landscapes vary from tropical jungles to high-altitude Andean areas. Historically rich, Antioquia's roots span from indigenous cultures to Spanish colonization. The region is famed for its \"paisa\" culture, characterized by warm hospitality and a strong work ethic. Antioquia is a major economic hub, combining industry with agriculture. Its cultural festivals, architectural gems, and innovative spirit make it a dynamic part of Colombia."
                        },
                        new State
                        {
                            Name = "Bogotá DC",
                            Cities = new List<City>
                            {
                                new City
                                {
                                    Name = "Bogotá"
                                }
                            },
                            Flag = "data:image/png;base64,",
                            Description = "Bogotá, D.C., is the capital and largest city of Colombia, situated in the Andean region. With an elevation of 2,640 meters, it's one of the highest capital cities globally. Known as a melting pot of Colombian diversity, it blends modern skyscrapers with historical colonial architecture. Cultural centers, museums, and theaters showcase its rich artistic heritage. Bogotá is Colombia's political, economic, and academic hub, driving innovation and development. Its vibrant nightlife, culinary scene, and annual festivals make it a dynamic urban center. Despite its bustling nature, green spaces like Simón Bolívar Park offer residents and visitors tranquil retreats."
                        },
                    }

                });
                _context.Countries.Add(new Country
                {
                    Name = "Nicaragua",
                    Flag = "data:image/png;base64,",
                    Capital = "Managua",
                    Description = "Nicaragua is the largest country in Central America, bordered by Honduras to the north and Costa Rica to the south. It boasts both Pacific and Caribbean coastlines. The country is known for its dramatic landscapes, which include lakes, volcanoes, and rainforests. Managua is its capital and largest city. Nicaragua has a rich cultural history influenced by indigenous and Spanish heritages. It's renowned for its traditional music, dance, and festivals. The nation has faced political challenges but remains a captivating destination for travelers."
                });
                _context.Countries.Add(new Country
                {
                    Name = "Honduras",
                    Flag = "data:image/png;base64,",
                    Capital = "Tegucigalpa",
                    Description = "Honduras is a Central American country bordered by Guatemala to the west, El Salvador to the southwest, Nicaragua to the southeast, and the Caribbean Sea to the north. Its landscape is characterized by mountains, rainforests, and coastal plains. Tegucigalpa is the capital and largest city. Honduras is known for its rich Mayan heritage, especially the ancient ruins of Copán. The country has a diverse culture influenced by indigenous, Spanish, and African heritages. It's also renowned for its natural beauty, including the Bay Islands, a popular diving destination."
                });

                await _context.SaveChangesAsync();
            }
        }
    }
}
